import React, { useState, useEffect } from "react";
import axios from "axios";

const AddDepartmentForm = () => {
  const [departmentName, setDepartmentName] = useState("");
  const [collegeId, setCollegeId] = useState("");
  const [colleges, setColleges] = useState([]);

  useEffect(() => {
    // Fetch colleges to populate dropdown
    const fetchColleges = async () => {
      const response = await axios.get("http://localhost:5000/colleges-admin");
      setColleges(response.data);
    };
    fetchColleges();
  }, []);

const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        // Make a POST request to add the department
        const response = await axios.post("http://localhost:5000/adddepartment", {
            department_name: departmentName,
            college_name: collegeName, // Corrected from collegeId to collegeName
        });

        // Display success message
        alert("Department added successfully: " + response.data.department.department_name);
        
        // Optionally, clear the form or reset state if needed
        setDepartmentName("");
        setCollegeName(""); // Make sure to reset this if needed
    } catch (error) {
        if (error.response) {
            // Handle specific error responses from the server
            if (error.response.status === 400) {
                alert("Error: " + error.response.data.message);
            } else if (error.response.status === 404) {
                alert("Error: " + error.response.data.message);
            } else {
                alert("An unexpected error occurred: " + error.response.data.message);
            }
        } else {
            // Handle network errors or unexpected issues
            alert("Network error or unexpected issue: " + error.message);
        }
    }
};


  return (
    <form onSubmit={handleSubmit}>
      <h3>Add New Department</h3>
      <div className="form-group">
        <label>Department Name</label>
        <input
          type="text"
          className="form-control"
          value={departmentName}
          onChange={(e) => setDepartmentName(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label>Select College</label>
        <select
          className="form-control"
          value={collegeId}
          onChange={(e) => setCollegeId(e.target.value)}
          required
        >
          <option value="">Select a College</option>
          {colleges.map((college) => (
            <option key={college.college_id} value={college.college_id}>
              {college.college_name}
            </option>
          ))}
        </select>
      </div>
      <button type="submit" className="btn btn-success mt-3">
        Add Department
      </button>
    </form>
  );
};

export default AddDepartmentForm;
