import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, ListGroup, Form, Table } from "react-bootstrap";
import dteImage from '..//images/dteimage.jpg';

const Home = () => {
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Redirect the user to the login page
      navigate("/"); // Adjust the route if needed
    } catch (error) {
      console.error("Error navigating to login:", error);
    }
  };

  return (
    <div>
      {/* Navigation Bar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-primary" style={{ background: "#2F71C4" }}>
        <div className="container-fluid">
          <a className="navbar-brand text-white font-weight-bolder" href="#">
            DTE
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link to={"/home"} className="btn btn-primary">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link to={"/showjob"} className="btn btn-primary">
                  Show Jobs
                </Link>
              </li>
            </ul>
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <a className="nav-link text-white" href="#" onClick={handleLogin}>
                  <i className="fas fa-sign-in-alt"></i> Login
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Home Page Content */}
      <div className="container mt-4">
        <h1>Welcome to the DTE Management Information System</h1>
        <p>This is the home page where you can access various functionalities of the system.</p>
        {/* Add more content or components as needed */}
      </div>

      
  {/* Visual Representations */}

  <Row>
  {/* Visual Representations */}
  <Col md={12} className="mb-4">
    <Card>
      <Card.Header>Visual Representations</Card.Header>
      <Card.Body>
        {/* Include the image here */}
        <img
          src={dteImage} // Replace with the actual path to your image
          alt="Description of the Image"
          style={{ width: '100%', height: '500px' }} // Adjust styles as needed
        />
      </Card.Body>
    </Card>
  </Col>
</Row>


  



    </div>
    
  );
};

export default Home;
