import React, { Fragment, useState } from "react";
import AddCollegeForm from "../pages/AddCollegeForm";
import AddDepartmentForm from "../pages/AddDepartmentForm";
import AddUserForm from "../pages/AddUserForm";

// Dummy data for users table
const users = [
  { id: 1, name: "John Doe", email: "john@example.com", role: "admin" },
  { id: 2, name: "Jane Smith", email: "jane@example.com", role: "user" },
];

const Admin = () => {
  const [selectedOption, setSelectedOption] = useState("");

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  return (
    <Fragment>
      {/* Navigation Bar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-primary" style={{ background: "#2F71C4" }}>
        <div className="container-fluid">
          <a className="navbar-brand text-white font-weight-bolder" href="#">DTE</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link text-white active" aria-current="page" href="#">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link text-white" href="#">Add Job</a>
              </li>
            </ul>
            <ul className='navbar-nav ml-auto'>
              <li className='nav-item'>
                <a className='nav-link text-white' href='#'>
                  <i className='fas fa-user'></i> Switch User
                </a>
              </li>
              <li className='nav-item'>
                <a className='nav-link text-white' href='#'>
                  <i className='fas fa-sign-out-alt'></i> Logout
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* User Information and Admin Panel Cards */}
      <div className="container mt-4">
        <div className="row justify-content-center">
          {/* User Information Card */}
          <div className="col-md-8 mb-4">
            <div className="card" style={{ backgroundColor: "#f8f9fa" }}>
              <div className="card-body">
                <h3 className="card-title mb-4 text-center">User Information</h3>
                <table className="table table-striped table-hover table-bordered">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Role</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map(user => (
                      <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Admin Panel Card */}
          <div className="col-md-8">
            <div className="card">
              <div className="card-body">
                <h2 className="card-title text-center mb-4">Admin Panel</h2>
                <div className="btn-group d-flex justify-content-center mb-4" role="group">
                  <button
                    className={`btn ${selectedOption === "college" ? "btn-primary" : "btn-secondary"}`}
                    onClick={() => handleOptionChange("college")}
                  >
                    Add New College
                  </button>
                  <button
                    className={`btn ${selectedOption === "department" ? "btn-primary" : "btn-secondary"}`}
                    onClick={() => handleOptionChange("department")}
                  >
                    Add New Department
                  </button>
                  <button
                    className={`btn ${selectedOption === "user" ? "btn-primary" : "btn-secondary"}`}
                    onClick={() => handleOptionChange("user")}
                  >
                    Add New User
                  </button>
                </div>

                {/* Render the corresponding form based on selected option */}
                {selectedOption === "college" && <AddCollegeForm />}
                {selectedOption === "department" && <AddDepartmentForm />}
                {selectedOption === "user" && <AddUserForm />}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default Admin;
