import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import JobForm from "./components/JobForm";
import ShowJob from "./pages/ShowJob";
import Login from "./components/Login";
import Register from "./components/Register";
import Admin from "./components/Admin";
import ProtectedRoute from "./components/ProtectedRoute";
import { AuthProvider } from "./components/AuthContext";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { ToastContainer } from "react-toastify";  // Import ToastContainer
import 'react-toastify/dist/ReactToastify.css';    // Import styles

function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Routes */}
          <Route
            path="/showjob"
            element={<ProtectedRoute component={ShowJob} roles={["user", "admin"]} />}
          />
          <Route
            path="/admin"
            element={<ProtectedRoute component={Admin} roles={["admin"]} />}
          />
          <Route
            path="/addjob"
            element={<ProtectedRoute component={JobForm} roles={["user", "admin"]} />}
          />
        </Routes>

        {/* Global Toast Container */}
        <ToastContainer />  {/* This ensures it's available app-wide */}
      </BrowserRouter>
    </AuthProvider>
  );
}

export default App;
