import React, { Fragment, useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';

const ShowJob = () => {
    const [college, setCollege] = useState('');
    const [colleges, setColleges] = useState([]);
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        async function fetchColleges() {
            try {
                const response = await fetch(`http://localhost:5000/get-colleges`);
                const data = await response.json();
                setColleges(data);
                if (data.length > 0) {
                    setCollege(data[0].college_name); // Select the first college by default
                }
            } catch (error) {
                console.error("Error fetching colleges:", error);
            }
        }
        fetchColleges();
    }, []);

    useEffect(() => {
        if (!college) return; // Avoid fetching if college is not set

        async function fetchJobs() {
            try {
                const response = await fetch(`http://localhost:5000/jobs-count?collegeName=${college}`);
                const data = await response.json();
                setJobs(data);
                console.log(response); // Check the response status
                console.log(data); // Check the data format
            } catch (err) {
                console.error(err.message);
            }            
        }
        fetchJobs();
    }, [college]);

    return (
        <Fragment>

            
            <div className="container-fluid p-0">
                <h1 className="text-center mt-5">Job Posts</h1>
                <div className="mb-3 text-center mt-3">
                    <label htmlFor="college" className="form-label">College:</label>
                    <select id="college" className="form-select" value={college} onChange={(e) => setCollege(e.target.value)}>
                        {colleges.map((c, index) => (
                            <option key={index} value={c.college_name}>{c.college_name}</option>
                        ))}
                    </select>
                </div>

                <Table striped bordered hover className="table mt-5 text-center">
                    <thead>
                        <tr>
                            <th>Job Title</th>
                            <th>Department</th>
                            <th>Count</th>
                        </tr>
                    </thead>
                    <tbody>
                        {jobs.map((job, index) => (
                            <tr key={index}>
                                <td>{job.job_title}</td>
                                <td>{job.department_name}</td>
                                <td>{job.job_count}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </Fragment>
    );
};

export default ShowJob;
