import React, { Fragment, useState, useEffect } from 'react';
import { Dropdown, Container, Table } from 'react-bootstrap';

const ShowJob = () => {
    const [state, setState] = useState('Kerala');
    const [district, setDistrict] = useState('');
    const [college, setCollege] = useState('');
    const [department, setDepartment] = useState('');
    const [jobrole, setJobrole] = useState('');

    const [colleges, setColleges] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [jobTitles, setJobTitles] = useState([]);
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        async function fetchDistricts() {
            try {
                const response = await fetch(`http://localhost:5000/districts?state=${state}`);
                const data = await response.json();
                setDistricts(data);
                if (data.length > 0) {
                    setDistrict(data[0].district); // Select the first district by default
                }
            } catch (error) {
                console.error("Error fetching districts:", error);
            }
        }
        fetchDistricts();
    }, [state]);

    useEffect(() => {
        async function fetchColleges() {
            try {
                const response = await fetch('http://localhost:5000/get-colleges');
                const data = await response.json();
                setColleges(data);
                if (data.length > 0) {
                    setCollege(data[0].college_name); // Select the first college by default
                }
            } catch (error) {
                console.error("Error fetching colleges:", error);
            }
        }
        fetchColleges();
    }, []);

    useEffect(() => {
        if (!college) return;
        async function fetchDepartments() {
            try {
                const response = await fetch(`http://localhost:5000/departments?college=${college}`);
                const data = await response.json();
                setDepartments(data);
            } catch (error) {
                console.error("Error fetching departments:", error);
            }
        }
        fetchDepartments();
    }, [college]);

    useEffect(() => {
        if (!college) return;
        async function fetchJobs() {
            try {
                const response = await fetch(`http://localhost:5000/jobs-count?collegeName=${college}`);
                const data = await response.json();
                setJobs(data);
            } catch (error) {
                console.error("Error fetching jobs:", error);
            }
        }
        fetchJobs();
    }, [college]);

    return (
        <Fragment>
            <div className="container-fluid p-0">
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <div className="container-fluid">
                        <a className="navbar-brand font-weight-bolder" href="#">DTE</a>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                                <li className="nav-item">
                                    <a className="nav-link active" aria-current="page" href="#">Home</a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#">Add Job</a>
                                </li>
                            </ul>
                            <ul className='navbar-nav ml-auto'>
                                <li className='nav-item'>
                                    <a className='nav-link' href='#'>
                                        <i className='fas fa-user'></i>Switch User
                                    </a>
                                </li>
                                <li className='nav-item'>
                                    <a className='nav-link' href='#'>
                                        <i className='fas fa-sign-out-alt'></i>Logout
                                    </a>
                                </li>
                            </ul> 
                        </div>
                    </div>
                </nav>

                <Container className="mt-4">
                    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>
                        <div style={{ display: 'flex', gap: '1rem' }}>
                            <Dropdown>
                                <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                                    District
                                </Dropdown.Toggle>
                                <Dropdown.Menu>
                                    {districts.map((d, index) => (
                                        <Dropdown.Item key={index} onClick={() => setDistrict(d.district)}>
                                            {d.district}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>

                            <Dropdown>
                                <Dropdown.Toggle variant="secondary" id="dropdown-basic-2">
                                    College
                                </Dropdown.Toggle>
                                <Dropdown.Menu>
                                    {colleges.map((c, index) => (
                                        <Dropdown.Item key={index} onClick={() => setCollege(c.college_name)}>
                                            {c.college_name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>

                            <Dropdown>
                                <Dropdown.Toggle variant="secondary" id="dropdown-basic-3">
                                    Department
                                </Dropdown.Toggle>
                                <Dropdown.Menu>
                                    {departments.map((d, index) => (
                                        <Dropdown.Item key={index} onClick={() => setDepartment(d.department_name)}>
                                            {d.department_name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>

                            <Dropdown>
                                <Dropdown.Toggle variant="secondary" id="dropdown-basic-4">
                                    Jobrole
                                </Dropdown.Toggle>
                                <Dropdown.Menu>
                                    <Dropdown.Item onClick={() => setJobrole('proff')}>proff</Dropdown.Item>
                                    <Dropdown.Item onClick={() => setJobrole('ass porff')}>ass porff</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                        </div>
                        <div style={{ display: 'flex', gap: '1rem' }}>
                            {district && (
                                <button
                                    type="button"
                                    className="btn btn-outline-secondary btn-sm"
                                    onClick={() => setDistrict('')} 
                                    aria-label="Close"
                                >
                                    {district} <span aria-hidden="true">&times;</span>
                                </button>
                            )}
                            {college && (
                                <button
                                    type="button"
                                    className="btn btn-outline-secondary btn-sm"
                                    onClick={() => setCollege('')} 
                                    aria-label="Close"
                                >
                                    {college} <span aria-hidden="true">&times;</span>
                                </button>
                            )}
                            {department && (
                                <button
                                    type="button"
                                    className="btn btn-outline-secondary btn-sm"
                                    onClick={() => setDepartment('')} 
                                    aria-label="Close"
                                >
                                    {department} <span aria-hidden="true">&times;</span>
                                </button>
                            )}
                            {jobrole && (
                                <button
                                    type="button"
                                    className="btn btn-outline-secondary btn-sm"
                                    onClick={() => setJobrole('')} 
                                    aria-label="Close"
                                >
                                    {jobrole} <span aria-hidden="true">&times;</span>
                                </button>
                            )}
                        </div>

                        <div className="mt-4" style={{ width: '100%', maxWidth: '800px' }}>
                            <Table striped bordered hover variant="light" className='table table-bordered'>
                                <thead>
                                    <tr>
                                        <th>College</th>
                                        <th>Department</th>
                                        <th>Jobrole</th>
                                        <th>Count</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {Array.from({ length: 5 }).map((_, index) => (
                                        <tr key={index}>
                                            <td>Row {index + 1} Col 1</td>
                                            <td>Row {index + 1} Col 2</td>
                                            <td>Row {index + 1} Col 3</td>
                                            <td>Row {index + 1} Col 4</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                    </div>
                </Container>

                <Table striped bordered hover className="table mt-5 text-center">
                    <thead>
                        <tr>
                            <th>Job Title</th>
                            <th>Department</th>
                            <th>Count</th>
                        </tr>
                    </thead>
                    <tbody>
                        {jobs.map((job, index) => (
                            <tr key={index}>
                                <td>{job.job_title}</td>
                                <td>{job.department_name}</td>
                                <td>{job.job_count}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </Fragment>
    );
};

export default ShowJob;
