import React, { Fragment, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/js/bootstrap.bundle.min.js"; // Ensure Bootstrap JS is included
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const AddUserForm = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/register", {
        name,
        email,
        password,
        role,
      });

      if (response.data.Status === "User Registered") {
        setSuccess("User registered successfully!");
        setError("");
        toast.success("User added successfully!", { autoClose: 3000 });

        // Delay the navigation to allow user to see the toast message
        // Delay the refresh to allow the user to see the toast message
  setTimeout(() => {
    window.location.reload();
  }, 3000);
        setTimeout(() => {
          navigate("/admin");
        }, 3000); 
      } else {
        setError(response.data.Error || "Error during registration.");
        toast.error("Error during registration.");
        setSuccess("");
        setShowModal(true); 
      }
    } catch (err) {
      console.error(err);
      setError("Registration failed");
      toast.error("Registration failed.");
      setSuccess("");
      setShowModal(true); 
    }
  };

  const handleCloseModal = () => setShowModal(false);

  return (
    <Fragment>
      <form onSubmit={handleSubmit}>
        <h3>Add New User</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Enter Name"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            className="form-control"
            placeholder="Enter Email Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            className="form-control"
            placeholder="Any Password of Your Choice"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <select
            className="form-control"
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit" className="btn btn-success mt-3">
          Add User
        </button>
        <ToastContainer />
      </form>

      {/* Modal for error messages */}
      {showModal && (
        <div
          className="modal fade show"
          tabIndex="-1"
          role="dialog"
          style={{ display: "block" }}
        >
          <div className="modal-dialog modal-sm" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Error</h5>
                <button
                  type="button"
                  className="btn-close"
                  aria-label="Close"
                  onClick={handleCloseModal}
                ></button>
              </div>
              <div className="modal-body">
                <p>{error}</p>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={handleCloseModal}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Fragment>
  );
};

export default AddUserForm;
