import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import AddCollegeForm from "../pages/AddCollegeForm";
import AddDepartmentForm from "../pages/AddDepartmentForm";
import AddUserForm from "../pages/AddUserForm";
import UserInformationTable from "../adminPage/UserInformationTable";
import CollegeInformationTable from "../adminPage/CollegeInformationTable";

const Admin = () => {
    const [selectedOption, setSelectedOption] = useState("");
    const [users, setUsers] = useState([]);
    const [colleges, setColleges] = useState([]);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:5000/users');
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchColleges = async () => {
        try {
            const response = await axios.get("http://localhost:5000/colleges-admin");
            setColleges(response.data);
        } catch (error) {
            console.error("Error fetching colleges:", error);
        }
    };

    useEffect(() => {
        fetchUsers();
        fetchColleges(); // Fetch colleges on component mount
    }, []);

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleCollegeFormSuccess = () => {
        console.log("College added successfully. Refreshing college list...");
        fetchColleges(); // Re-fetch colleges after successful addition
        setSelectedOption("");
    };

    return (
        <Fragment>
            {/* Your navigation bar and other components */}
            
            <div className="container mt-4">
                <div className="row justify-content-center">
                    <div className="col-md-10">
                        <div className="card">
                            <div className="card-body">
                                <h2 className="card-title text-center mb-4">Admin Panel</h2>
                                <div className="btn-group d-flex justify-content-center mb-4" role="group">
                                    <button className={`btn ${selectedOption === "college" ? "btn-primary" : "btn-secondary"}`} onClick={() => handleOptionChange("college")}>Add New College</button>
                                    <button className={`btn ${selectedOption === "department" ? "btn-primary" : "btn-secondary"}`} onClick={() => handleOptionChange("department")}>Add New Department</button>
                                    <button className={`btn ${selectedOption === "user" ? "btn-primary" : "btn-secondary"}`} onClick={() => handleOptionChange("user")}>Add New User</button>
                                </div>

                                {/* Render the corresponding form based on selected option */}
                                {selectedOption === "college" && <AddCollegeForm onSuccess={handleCollegeFormSuccess} />}
                                {selectedOption === "department" && <AddDepartmentForm />}
                                {selectedOption === "user" && <AddUserForm />}
                            </div>
                        </div>
                    </div>

                    {/* College Information Card */}
                    <div className="col-md-10 mb-4">
                        <CollegeInformationTable colleges={colleges} /> {/* Pass colleges as prop */}
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default Admin;
