import React, { useState, useEffect } from "react";
import axios from "axios";

const AddDepartmentForm = () => {
  const [departmentName, setDepartmentName] = useState("");
  const [collegeId, setCollegeId] = useState("");
  const [colleges, setColleges] = useState([]);

  useEffect(() => {
    // Fetch colleges to populate dropdown
    const fetchColleges = async () => {
      const response = await axios.get("http://localhost:5000/colleges-admin");
      setColleges(response.data);
    };
    fetchColleges();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        // Find the selected college name from the colleges array using the collegeId
        const selectedCollege = colleges.find(college => college.college_id === collegeId);
        
        // Ensure selected college is found before proceeding
        if (!selectedCollege) {
            alert("Please select a valid college.");
            return;
        }

        await axios.post("http://localhost:5000/adddepartment", {
    department_name: departmentName,
    college_name: selectedCollege.college_name, // Ensure this is correct
});

        console.log(response.data.message);
        if (response.data.message === "Department already exists for this college") {
            alert(response.data.message); // Show specific error message
        } else if (response.data.message === 'Department added successfully') {
            alert(response.data.message); // Show success message
        } else {
            alert("Unexpected response. Please try again."); // Fallback for unexpected responses
        }
    } catch (error) {
        console.error(error);
        if (error.response && error.response.data && error.response.data.message) {
            alert(error.response.data.message);
        } else {
            alert("An error occurred. Please try again.");
        }
    }
};


  return (
    <form onSubmit={handleSubmit}>
      <h3>Add New Department</h3>
      <div className="form-group">
        <label>Department Name</label>
<select
  className="form-control"
  value={departmentName}
  onChange={(e) => setDepartmentName(e.target.value)}
  required
>
  <option value="">Select a Department</option>
  <option value="Computer Science and Engineering">Computer Science and Engineering</option>
  <option value="Mechanical Engineering">Mechanical Engineering</option>
  <option value="Instrumentation and Control Engineering">Instrumentation and Control Engineering</option>
  <option value="Electrical and Electronics Engineering">Electrical and Electronics Engineering</option>
  <option value="Civil Engineering">Civil Engineering</option>
  <option value="Electronics and Communication Engineering">Electronics and Communication Engineering</option>
  <option value="Information Technology">Information Technology</option>
</select>

      </div>
      <div className="form-group">
        <label>Select College</label>
        <select
          className="form-control"
          value={collegeId}
          onChange={(e) => setCollegeId(e.target.value)}
          required
        >
          <option value="">Select a College</option>
          {colleges.map((college) => (
            <option key={college.college_id} value={college.college_id}>
              {college.college_name}
            </option>
          ))}
        </select>
      </div>
      <button type="submit" className="btn btn-success mt-3">
        Add Department
      </button>
    </form>
  );
};

export default AddDepartmentForm;
