                </div>

                <div className="mb-3">
                    <label htmlFor="job_title" className="form-label">Job Title:</label>
                    <input
                        type="text"
                        id="job_title"
                        className="form-control"
                        value={jobTitle}
                        onChange={(e) => setJobTitle(e.target.value)}
                        required
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="job_description" className="form-label">Job Description:</label>
                    <textarea
                        id="job_description"
                        className="form-control"
                        value={jobDescription}
                        onChange={(e) => setJobDescription(e.target.value)}
                        rows="3"
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor="eligibility" className="form-label">Eligibility:</label>
                    <textarea
                        id="eligibility"
                        className="form-control"
                        value={eligibility}
                        onChange={(e) => setEligibility(e.target.value)}
                        rows="3"
                    />
                </div>

                <button type="submit" className="btn btn-primary">Add Job Post</button>
            </form>
        </Fragment>
    );
};

export default JobForm;
